package CursoMilProgramadores;

public class ejercicio_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int divisor=10;
		int dividendo=25;
		
		int resto=dividendo % divisor;
		
		System.out.println("el resto entre dividendo y divisor es: "+resto);
		
		dividendo=25/10;
		resto=dividendo % divisor;
		System.out.println("el nuevo resto entre dividendo y divisor es: "+resto);
		
	}

}
